<?PHP







$ofile = opendir($link);
echo "<div style='text-align: left'>";
while (($file = readdir($ofile)) !== false) {
    if($file == "." OR $file == ".htaccess" OR (basename(__FILE__) == "$file" AND $link == "./")){ continue; }

      if(filetype($link.$file) == "dir"){
              if($file == ".."){
                if($link != "./"){
                    echo "<li><a href='?dir=".$lina."'>".$file."</a></li>";
                }
              }else{
                  echo "<li><a href='?dir=".$link.$file."/'>".$file."</a></li>";
              }

      }else{
               if(substr($file,-3) == "php"){$class = "phptools";}else{$class = "tools";}
               $FSize = filesize ($link.$file)/1024;
               if($FSize > 1024){
                    $FSize =  round($FSize/1024,2)."M";

               }else{
                    $FSize =  round($FSize,2)."K";
               }
              echo "<li class='".$class."'><a href='".$link.$file."'>".$file."</a> (".$FSize.")</li>";
      }

if($file == "index.html" OR $file == "index.php"){header("Location: ".$link.$file);die();} 

}
echo "</div>";
