function FileFormt($fdata){
  if (0 > $fdata){$fdata = $fdata * -1; }
    if($fdata < 1024){
      return $fdata." B";
    }elseif($fdata < (1024*1024)){
       return round($fdata/1024)." KB";
    }elseif($fdata < (1024*1024*1024)){
       return round($fdata/(1024*1024),1)." MB";
    }elseif($fdata < (1024*1024*1024*1024)){
       return round($fdata/(1024*1024*1024),2)." GB";
    }else{
       return $fdata;
    }
}

